var bunyan = require('bunyan');
var name = 'dataTransit_' + new Date().toLocaleDateString();
var path = './logs/dataTransit_' + new Date().toLocaleDateString() + '.log';
var logger = bunyan.createLogger({
    name: name,
    streams: [{
        level: 'info',
        type: 'rotating-file',
        path: path,
        period: '1d', // daily rotation
        count: 3650 // keep 3650 back copies
    }]
});
console.log(name);
module.exports.logger = logger;